function fiboEvenSum(n) {
  var fibo = [1, 2];
  // Fibonacci sequence containing the first two terms.
  var sum = 0;
  // Sum of even Fibonacci Numbers.
  for (var i = 2; i <= n; i++) 
  // Computing the Fibonacci sequence starting from the 3rd term to the n-th term. 
  { 
    fibo.push(fibo[i - 1] + fibo[i - 2]);
  };
  for (var m = 1; m <= n; m += 3) 
  /* 
  Computing the sum of even Fibonacci sequence.
  All the even Fibonacci term lies in the (2 + 3*n)-th term, where n is a non-negative integer.
  */
  { 
    sum += fibo[m];
  };
  return sum;
  // Prints out the sum. 
}

fiboEvenSum(10);
