function largestPrimeFactor(number) {
  var maxpfactor = 1;
  while (number % 2 == 0) {
    number /= 2;
    maxpfactor = 2;
  }
  for (var pfactor = 3; pfactor < Math.sqrt(number); pfactor += 2) {
    while (number % pfactor == 0) {
      number /= pfactor;
      maxpfactor = pfactor;
    };
  };
  if (pfactor <= number) {
    maxpfactor = number;
  };
  return maxpfactor;
}

